// 테이블 생성
CREATE TABLE IF NOT EXISTS DOT_INFO (DOT_SEQ INTEGER, DOT_ID TEXT, DOT_TITLE TEXT , DOT_DESC TEXT, DOT_MNG_TITLE TEXT , DOT_MNG_DESC TEXT, DOT_TY TEXT , USE_YN TEXT DEFAULT 'Y', OFFLINE_YN TEXT DEFAULT 'N', LOOKUP_CNT INTEGER DEFAULT 0, NEXT_DOT_ID TEXT , MAX_RECOG_CNT INTEGER DEFAULT 1, DELAY_TM INTEGER DEFAULT 0, SERVICE_TARGET TEXT , DOWNLOAD_URL TEXT , DEFAULT_LANG_CD TEXT DEFAULT 'ko', MULTI_LANG_YN TEXT DEFAULT 'N', START_DT TEXT DEFAULT '00000000000000', FINISH_DT TEXT DEFAULT '99991231235959', SERVICE_TY TEXT , ODROID_SIGNAL_YN TEXT DEFAULT 'N', ORG_SERVICE_ID TEXT, CMPNY_CD TEXT, CREAT_USER_CD TEXT, CREAT_DT TEXT, UPDT_USER_CD TEXT, UPDT_DT TEXT, DISPLAY_ORIENTATION TEXT DEFAULT 'P', ADD_METADATA_INFO TEXT, PRIMARY KEY (DOT_SEQ))
CREATE TABLE IF NOT EXISTS DOT_METADATA (DOT_SEQ INTEGER, LANG_CD TEXT, DOT_TITLE TEXT, DOT_METADATA_DESC TEXT, CREAT_USER_CD TEXT, CREAT_DT TEXT, UPDT_USER_CD TEXT, UPDT_DT TEXT, PRIMARY KEY (DOT_SEQ, LANG_CD))
CREATE TABLE IF NOT EXISTS DOT_OFFLINE_INFO (DOT_SEQ INTEGER, DOT_OFFLINE_SEQ INTEGER, DOT_FILE_PATH TEXT, FILE_NAME TEXT, ORG_FILE_NAME TEXT, FILE_CONNECT_URI TEXT, FILE_DESC TEXT, CONTENT_TY TEXT, DOWNLOAD_FILE_YN TEXT DEFAULT 'N', ACTION_TY TEXT, CREAT_USER_CD TEXT, CREAT_DT TEXT, UPDT_USER_CD TEXT, UPDT_DT TEXT, PRIMARY KEY (DOT_SEQ, DOT_OFFLINE_SEQ))
CREATE TABLE IF NOT EXISTS DOT_SCAN_HISTORY (H_SEQ INTEGER PRIMARY KEY AUTOINCREMENT, DOT_SEQ INTEGER, DOT_TY TEXT, DOT_ID TEXT, DOT_TITLE TEXT, DOT_SUBTITLE TEXT, DOT_DESC TEXT, SERVICE_TARGET TEXT, READ_YN TEXT, CONTENT_TY TEXT, ACTION_TY TEXT, FILE_CONNECT_URI TEXT, DISPLAY_ORIENTATION TEXT DEFAULT 'P', ODROID_SIGNAL_YN TEXT DEFAULT 'N', ADD_METADATA_INFO TEXT, FILE_NAME TEXT, SCAN_DATE TEXT, THUMBNAIL TEXT)
